$(function () {

    $body = $('body');
    //donor sign up form validation logic
    $('#dsignup').validate({
        rules: {
            firstname: {
                required: true,
                minlength: 2
            },
            lastname: {
                required: true,
                minlength: 2
            },
            email: {
                required: true,
                email: true
            },
            password: {
                required: true,
                minlength: 5
            }
        },
        messages: {
            firstname: {
                required: "Please enter your firstname.",
                minlength: "Firstname must have 2 atleast characters."
            },
            lastname: {
                required: "Please enter your lastname.",
                minlength: "Lastname must have 2 atleast characters."
            },
            email: "Pease enter valid email.",
            password: {
                required: "Please enter your password.",
                minlength: "Password must have 5 at least characters."
            }
        },
        submitHandler: function () {

            $('#exampleModal1').modal({
                keyboard: false,
                backdrop: 'static'
            });
        }
    });


    $('.sw-btn-next.btn.sw-btn-next[disabled]').css("background-color", 'rgb(209,209,209)').css('color', '#666');
    $('.sw-btn-next').attr("disabled", "disabled");

    $('[name="is_female"]').on("click", function () {

        if (check_radio($(this), "yes")) {
            $('.is_female_err').css("opacity", 0);
            $body.find('.sw-btn-next').removeAttr("disabled");
        } else {
            $('.is_female_err').css("opacity", 1);
            $body.find('.sw-btn-next').attr('disabled', 'disabled');
        }
    });


    $('input#age').on("change", function (evt) {

        if ($(this).val() !== "") {
            $('.sw-btn-next').removeAttr("disabled");
        } else {
            $('.sw-btn-next').attr("disabled", "disabled");
        }
    });

    $('[name="had_pelvic"]').on("click", function () {
        if (check_radio($(this), "yes")) {
            $('.sw-btn-next').removeAttr("disabled");
        } else {
            $('.sw-btn-next').attr('disabled', 'disabled');
        }
    });

    $('[name="are_willing"]').on("click", function () {
        if (check_radio($(this), "yes")) {
            $('.are_willing_err').css("opacity", 0);
            $('.sw-btn-next').removeAttr("disabled");
        } else {
            $('.are_willing_err').css("opacity", 1);
            $('.sw-btn-next').attr('disabled', 'disabled');
        }
    });

    $('[name="are_able"]').on("click", function () {
        if (check_radio($(this), "yes")) {
            $('.sw-btn-next').removeAttr("disabled");
        } else {
            $('.sw-btn-next').attr('disabled', 'disabled');
        }

    });
});

function check_radio(to_check, value_check) {
    return to_check.val() === value_check;
}
